-- Fix Pro tier: $500/month founding (was $697/month regular)
UPDATE membershipTiers 
SET priceMonthly = 69700,
    foundingPriceMonthly = 50000
WHERE slug = 'pro';

SELECT slug, name, priceMonthly, foundingPriceMonthly FROM membershipTiers WHERE slug = 'pro';

